mapper.update:
update huiyuan_activity
set status=0, updated_time=now()
where huiyuan_id=#{huiyuanId} and activity_id=#{activityId}

mapper.select:
select count(*)
from huiyuan_activity
where activity_id = #{id} and volunteer_apply_status = 0 and status=1;

select count(*)
from huiyuan_activity
where activity_id = #{id} and volunteer_apply_status = 1 and status=1;
